import React, { useEffect } from "react";
import { Container, CircularProgress, Typography } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";

import { movieResult as movieResultSelector } from "../../redux/selectors";
import { searchMovieById } from "../../redux/actions/search";

export default ({ match }) => {
  const dispatch = useDispatch();
  const movieResult = useSelector((state) => movieResultSelector(state));

  console.log("movieResult");
  console.log(movieResult);

  useEffect(() => {
    console.log("match");
    console.log(match);

    if (!movieResult) {
      const movieId = match.params.id;
      dispatch(searchMovieById({ movieId }));
      console.log("movieId");
      console.log(movieId);
      console.log("movieResult");
      console.log(movieResult);
    }
  });
  if (!movieResult) {
    return <CircularProgress size={100} color="primary" />;
  }
  return (
    <Container>
      <Typography variant="h3">{movieResult.Title}</Typography>
      <img src={movieResult.Poster} alt={movieResult.Title} />
      <Typography>
        <strong>Actores:</strong>
        {movieResult.Actors}
      </Typography>
      <Typography>
        <strong>Director:</strong>
        {movieResult.Director}
      </Typography>
      <Typography>
        <strong>Pais:</strong>
        {movieResult.Country}
      </Typography>
      <Typography>
        <strong>Clasificacion:</strong>
        {movieResult.Rated}
      </Typography>
      <Typography>
        <strong>Premios:</strong>
        {movieResult.Awards}
      </Typography>
      <Typography>
        <strong>Sipnosis:</strong>
        {movieResult.Plot}
      </Typography>
    </Container>
  );
};
