import {makeStyles} from '@material-ui/styles';

const centeredStyleObj={
    display:'flex',
    alignItems:'center',
    justifyContent:'center'
}

export default makeStyles({
    container:{
        height:'100vh',
        flexDirection:'column',
        ...centeredStyleObj
    },
    cardContainer:{
        flexDirection:'column',
        width:400,
        height:200,
        padding:'2rem',
        ...centeredStyleObj,
    }
});
